from django.urls import pathfrom . import viewsurlpatterns= [    path('',views.index, name = "index"),    # mysite의 post_list라는 views가 URL에 할당됨    path('mysite/',views.index, name = 'mysite'),    # 뒤에 name을 붙이는 이유는 뷰의 이름이 같을 수도 있기 때문에 URL에 고유한 이름을 붙이는것이라고 합니다.    path('mysite/new/',views.post, name = "new_lottos"),    path('mysite/?P<mysitekey>([0-9]+)/detail/',views.detail, name = 'mysite_detail'),]